-- disable all constraints
DECLARE @tname VARCHAR(128), 
	@tschema VARCHAR(128);

DECLARE tables CURSOR FOR
	SELECT TABLE_SCHEMA, TABLE_NAME
	FROM INFORMATION_SCHEMA.TABLES;

OPEN tables

FETCH NEXT FROM tables
    INTO @tschema, @tname

WHILE @@FETCH_STATUS = 0
BEGIN
	PRINT 'Disabling constraints for table: [' + @tschema + '].[' + @tname + ']'
    EXECUTE ('ALTER TABLE [' + @tschema + '].[' + @tname + '] NOCHECK CONSTRAINT ALL');
    FETCH NEXT FROM tables
        INTO @tschema, @tname;
end;

CLOSE tables
DEALLOCATE tables

-- delete better cms installed default data
DECLARE @pageId UNIQUEIDENTIFIER
SELECT @pageId = [Id] FROM [bcms_root].[Pages] WHERE [PageUrl] = N'/'

DELETE h
FROM [bcms_root].[Contents] c
	INNER JOIN [bcms_pages].[HtmlContents] h
		ON h.[Id] = c.[Id]
	INNER JOIN [bcms_root].[PageContents] pc
		ON pc.[ContentId] = c.[Id]
			AND pc.[PageId] = @pageId

DELETE c
FROM [bcms_root].[Contents] c
	INNER JOIN [bcms_pages].[HtmlContents] h
		ON h.[Id] = c.[Id]
	INNER JOIN [bcms_root].[PageContents] pc
		ON pc.[ContentId] = c.[Id]
			AND pc.[PageId] = @pageId


DELETE [bcms_root].[PageContents] WHERE [PageId] = @pageId
DELETE [bcms_pages].[Pages] WHERE [Id] = @pageId
