DECLARE tables CURSOR FOR
	SELECT TABLE_SCHEMA, TABLE_NAME
	FROM INFORMATION_SCHEMA.TABLES;

OPEN tables

FETCH NEXT FROM tables
    INTO @tschema, @tname

WHILE @@FETCH_STATUS = 0
BEGIN
	PRINT 'Enabling constraints for table: [' + @tschema + '].[' + @tname + ']'
